import apiClient from '../api/apiClient';
import { Message } from '../types/models';

export async function getMessages(username?: string): Promise<Message[]> {
  const url = username ? `/messages/user/${username}` : '/messages/all';
  const res = await apiClient.get<Message[]>(url);
  return res.data;
}

export async function askAI(content: string): Promise<Message> {
  const res = await apiClient.post<Message>('/chat/ask', { content });
  return res.data;
}
