import React from 'react';

type Props = {
  msg: Message;
};

const ChatMessage: React.FC<Props> = ({ msg }) => (
  <div className="p-3 mb-4 rounded shadow-sm bg-white border">
    <div className="text-end mb-2">
      <strong className="text-blue-700">You:</strong> {msg.content}
    </div>
    <div className="text-start">
      <strong className="text-green-700">AI:</strong> {msg.response}
    </div>
  </div>
);

export default ChatMessage;
