import React from 'react';
import { Message } from '../types/models';

type Props = {
  msg: Message;
};

const ChatMessage: React.FC<Props> = ({ msg }) => (
    <div className="input-group mt-3">
  <div className="bg-white border rounded-lg p-4 mb-4 shadow-md max-w-3xl mx-auto">
    <div className="text-sm text-gray-500 mb-1 font-semibold">You:</div>
    <div className="text-gray-800 mb-3 whitespace-pre-wrap">{msg.content}</div>

    <hr className="my-2 border-gray-200" />

    <div className="text-sm text-gray-500 mb-1 font-semibold">AI:</div>
    <div className="text-gray-800 whitespace-pre-wrap">{msg.response}</div>
  </div></div>
);

export default ChatMessage;
