import React, { useState, useEffect, KeyboardEvent } from 'react';
import { User, Message } from '../types/models';
import { getMessages, askAI } from '../services/chatService';
import ChatMessage from '../components/ChatMessage';

interface Props {
  user: User;
  onLogout: () => void;
}

const ChatPage: React.FC<Props> = ({ user, onLogout }) => {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);

  useEffect(() => {
    getMessages(user.username).then(setMessages);
  }, [user]);

 const sendMessage = async () => {
  if (!input.trim()) return;

  const userMsg: Message = {
    content: input,
    isFromUser: true
  };

  // Send to backend with userId and content
  const aiResponse = await askAI({
    userId: user.id,   // make sure user has .id
    content: input
  });

  setMessages(prev => [...prev, userMsg, aiResponse]);
  setInput('');
};

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') sendMessage();
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between mb-3">
        <h5>User: {user.username}</h5>
        <button className="btn btn-danger btn-sm" onClick={onLogout}>Logout</button>
      </div>

      <div style={{ height: 400, overflowY: 'auto', border: '1px solid #ccc', padding: 10 }}>
        {messages.map((msg, idx) => <ChatMessage key={idx} msg={msg} />)}
      </div>

      <div className="input-group mt-3">
        <input
          type="text"
          className="form-control"
          placeholder="Ask a question..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <button className="btn btn-primary" onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default ChatPage;
