import React from 'react';
import { Message } from '../types/models';

interface Props {
  msg: Message;
}

const ChatMessage: React.FC<Props> = ({ msg }) => (
  <div className={`mb-2 ${msg.isFromUser ? 'text-end' : 'text-start'}`}>
    <strong>{msg.isFromUser ? 'You' : 'AI'}:</strong> {msg.content || msg.response}
  </div>
);

export default ChatMessage;
