import React from 'react';
import { Message } from '../types/models';

type Props = {
  msg: Message;
};

const ChatMessage: React.FC<Props> = ({ msg }) => (
  <div className="mb-6">
    {/* User message bubble */}
    <div className="bg-blue-100 text-black p-3 rounded-lg max-w-[80%] ml-auto text-right shadow">
      <div className="text-sm font-bold mb-1">You</div>
      <div>{msg.content}</div>
    </div>

    {/* AI response bubble */}
    <div className="bg-gray-100 text-black p-3 rounded-lg max-w-[80%] mr-auto mt-2 text-left shadow">
      <div className="text-sm font-bold mb-1">AI</div>
      <div>{msg.response}</div>
    </div>
  </div>
);

export default ChatMessage;
