import React from 'react';
import { Message } from '../types/models';

type Props = {
  msg: Message;
};

const ChatMessage: React.FC<Props> = ({ msg }) => (
  <div className="border border-gray-300 rounded-md p-3 mb-3 bg-white max-w-3xl mx-auto text-sm shadow-sm">
    <div className="font-medium text-muted mb-1">You:</div>
    <div className="mb-2 whitespace-pre-wrap">{msg.content}</div>

    <div className="border-t border-gray-200 my-2" />

    <div className="font-medium text-muted mb-1">AI:</div>
    <div className="whitespace-pre-wrap">{msg.response}</div>
  </div>
);
export default ChatMessage;
